import { Button, Grid, Stack } from "@mui/material";
import React from "react";
import { useState } from "react";
import { DragDropContext, Droppable } from "react-beautiful-dnd";
import { AddTaskCardButton } from "./AddTaskCardButton";

import {TaskCard} from "./TaskCard"

const cardReorder=(taskCardsList, startIndex, endIndex)=>{
    const remove = taskCardsList.splice(startIndex, 1);
    taskCardsList.splice(endIndex, 0, remove[0])
};



export const TaskCards = () => {
    const [taskCardsList, setTaskCardsList] = useState([]);


    const handleDragEnd = (result) => {
        cardReorder(taskCardsList, result.source.index, result.destination.index);
        setTaskCardsList(taskCardsList);
    };

    const saveTask = () => {
        if(window.confirm("タスクの状態を保存しますか？ \n ※削除されたタスクは復元されません")){
          }
    }


    return (
    <div className="taskCardsArea">
        <Stack 
        sx={{height:60,width:"100%"}} 
        direction="row"
        >
        <AddTaskCardButton
            taskCardsList={taskCardsList}
            setTaskCardsList={setTaskCardsList}
        />
        <Button 
            style={{marginLeft:"auto"}}
            color="success" 
            variant="contained"
            onClick={()=> saveTask()}>保存
        </Button>
       
        </Stack>
        
       <DragDropContext onDragEnd={handleDragEnd}>
           
           <Droppable droppableId="droppable" direction="horizontal">
              {(provided) => (
                <div 
                  {...provided.droppableProps} 
                  ref={provided.innerRef}
                  >
                 <Grid 
                    container 
                    columns={{sm: 12, md: 12, lg: 12 }}
                    spacing={{sm: 4, md: 3, lg: 2 }} 
                    justifyContent="center"
                    alignItems="center"
                 >
                {taskCardsList.map((taskCard, index) => (
                  <Grid item  
                    key={index}
                    sm={12} md={6} lg={4}
                    >
                       <TaskCard
                            key={taskCard.id}
                            index={index}
                            taskCardsList={taskCardsList}
                            setTaskCardsList={setTaskCardsList}
                            taskCard={taskCard}
                        />
                  </Grid>
                  ))}
                   {provided.placeholder}

                </Grid>
                      
              </div>
              )}

           </Droppable>
        </DragDropContext>
        </div>


    )
}
