import { Box, Stack, Typography } from "@mui/material";
import React, { useState, useContext, useEffect, useMemo } from "react";
import { AuthContext } from "../../contexts/auth";

import ChatTemplate from "./ChatTemplate"

const height =  document.documentElement.clientHeight;

export const ChatMessages = ({
    messages, 
    selected, 
    cancelSend ,
    tempId,
}) => {
    let date = new Date().toString()
    let currentDate = ""
   return (
        <Box  
            sx={{padding:"150px 2rem 0",
            height:`${height-100}px`,
            width:"100%",
            overflow: "scroll"
            }}
            >
             {messages.map((message, index) => {
                currentDate = new Date(message.created_at)
            
                const side = message.employee_id === tempId ? "right" : "left";
                const baseTime = new Date(message.created_at)
                const time = `${("00"+baseTime.getHours()).slice(-2)} :${("00" + baseTime.getMinutes()).slice(-2)}`
                date = (new Date(new Date(message.created_at).getFullYear(),new Date(message.created_at).getMonth(),new Date(message.created_at).getDate()) > currentDate) ?
                    new Date(message.created_at).toString()
                :
                ""
                
                console.log(currentDate)
                console.log(new Date(new Date(message.created_at).getFullYear(),new Date(message.created_at).getMonth(),new Date(message.created_at).getDate()) )
                console.log(date)
                return (
                <div key={index}>
                    <Typography  variant="text">{date}</Typography>
                     <ChatTemplate
                        message={message}
                        roomId={selected.id}
                        side={side}
                        time={time}
                        cancelSend={cancelSend}
                    />
                </div>
                )
                })}

        </Box>)


}
