import { Button, CircularProgress, Slider, Typography } from "@mui/material";
import React, { useEffect, useState, useContext } from "react";
import { assignShift, deleteShift, determineShifts, fetchSubmittedShifts } from "../apis/shifts";
import { PageLoadingCircle } from "../components/shared/commonPatrs";
import { BadgeContext } from "../contexts/badge";
import { SnackbarContext } from '../contexts/snackBar';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import { Stack } from "@mui/system";
import { myShifts } from '../urls/urlList';
import { set } from 'react-hook-form';

import { ShiftEdit } from "./ShiftEdit";
import { dayOfWeek, SliderScales, STYLE_2 } from '../components/const';
import { DefaultModal } from "../components/shared/DefaultModal";
import { NewShiftForm } from "../components/shifts/NewShiftForm";
import { NewShiftModal } from "../components/shifts/NewShiftModal";
import { getMonAndDate } from "../lib/calcDate";
import { ConfigContext } from "../contexts/config";



export const Shift = () => {

  
const today = new Date();

const [value, setValue] = useState();
const [loading, setLoading] = useState(false)
const [selectedShift, setSelectedShift] = useState();

const [open, setOpen] = useState(false);
const [content, setContent] = useState("");
const sb = useContext(SnackbarContext);
const badge = useContext(BadgeContext);
const [period, setPeriod] = useState([today.getFullYear(),today.getMonth()+1]);

const undo = () => {
  fetchSubmittedShifts()
  .then(res =>  setValue(res.data.shifts))
}

const unassignShift = (params) => {
  
}

  
const updateShifts = (params) => {
  setLoading(true)
  determineShifts(params)
  .then(res => {
        if(res.status !== 200){ return sb.setSnackBar({open: true, variant:"error",content:"シフトを確定できませんでした。"}) }
        setValue(res.data.shifts)
        sb.setSnackBar({open: true, variant:"success",content:"シフトを確定しました。"})
        badge.setBadge({
          ...badge.badge, shift: res.data.shifts.filter(elm => elm.confirmed == false).length
        })
  })
  setLoading(false)
}

const renderMonth = (period) => {
  const days = [];
  const year = period[0],
  　　　 month = period[1]
  const lastDay = new Date(year, month,0);

  for(let i=1;i<=lastDay.getDate();i++){
    const targetDay = new Date(year, month,i);
    days.push(
      {id: i,
       date: targetDay,
       dow:`(${dayOfWeek[targetDay.getDay()]})`,
       minWidth: 100
      }
    )
  }

  return days;

}
const [days, setDays] = useState(renderMonth(period))
const orgConfig = useContext(ConfigContext);

const collectShift = (params) => {
  const ids =  [...new Set(value.map(s => s.employee_id))]
  const list = []
  ids.map(id => {
    list.push(params.filter(s => s.employee_id == id));
  })

  return list;
}

const handleValue = (e, newValue, activeThumb) => {
  
  if (!Array.isArray(newValue)) {
      return;
    }
    
    const i = change.indexOf(change.find(v => v.id == [e.target.name]))

    if (activeThumb === 0) {
      change[i].attendance_time = Math.min(newValue[0], e.target.value[1] - orgConfig.params.min_work_time)
      dispatch(change);
    } else {
      change[i].leaving_time = Math.max(newValue[1], e.target.value[0] + 2)
      dispatch(change);
    }
  }

const handleSubmit = (params) => {
  if(window.confirm(
    `シフトを確定しますか？\n※編集された場合従業員に通知されます。`
    )){updateShifts(state)}

}

const shiftClick = (params) => {
  const content = 
  <form onSubmit={handleSubmit}>
  <Stack style={{width:"90%", margin:"auto"}} spacing={2}>
      <label>
     
     

  </label>
    <Typography variant="h6">
      {params.name}
  </Typography>
   <Typography variant="h6">
      {getMonAndDate(new Date(params.date),"/")}
  </Typography>

  <Slider
      name={params.id}
      value={[params.attendance_time, params.leaving_time]}
      onChange={(e, newValue, activeThumb)=>handleValue(e, newValue, activeThumb)}
      marks={SliderScales}
      valueLabelDisplay="auto"
      min={orgConfig.params.open}
      max={orgConfig.params.close}
      step={0.5}
  />

  <Button
      fullWidth
      disabled={params.id ===""}
      type="submit"
      variant="contained"
      color="success"
      endIcon={loading? 
          <CircularProgress style={{width:"1.2rem", height: "1.2rem"}}/> :
           ""}
  >
      シフトを作成する
  </Button>
 

  </Stack>


  </form>;
  setContent(content)
  setOpen(!open)
  
}


useEffect(() => {
  try {
      fetchSubmittedShifts(period)
      .then((res) => {
      setValue(res.data.shifts.sort((ago, next)=>ago.date - next.date))
      console.log(res.data.shifts)
})
} 
catch(e){
  console.log(e)
}

},[])

  return (
    <>
    {
    value ?

    <div>
      <DefaultModal
        open={open}
        setOpen={setOpen}
        content={content}
      />
      <h2>{period[0]}-{period[1]}</h2>
      <TableContainer style={{maxHeight: 400}}>
            <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              <TableCell
                  style={{ minWidth: 50 }}
                >
                  <span>従業員名</span>
                </TableCell>
              {days.map(column => (
                <TableCell
                  key={column.id}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.date.getDate().toString()}{column.dow}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
          {
          collectShift(value).map((row,i) => (
            <TableRow key={i}>
              <TableCell
                  key={i}
                >
                  {row[0].name}
                </TableCell>
             {days.map((elm,i) =>   
                  
                   (
                    (()=>{
                      const index = row.findIndex(e => new Date(e.date).getDate() == elm.date.getDate())
                      return index >= 0 ?
                      <TableCell
                        key={i}
                        style={{"background":"pink"}}
                        onClick={()=>shiftClick(row[index])}
                      >{row[index].attendance_time}-{row[index].leaving_time}
                     </TableCell>
                       :
                      <TableCell 
                        key={i} 
                        style={{"background":"gray"}}
                        onClick={()=>shiftClick(row[index])}
                      >休</TableCell>
                    })()
                  )            
               
              )}  
            </TableRow>
          
            ))
          }
        </TableBody>
        </Table>
        </TableContainer>
      <ShiftEdit
        value={value}
        loading={loading}
        setLoading={setLoading}
        undo={undo}
        updateShifts={updateShifts}
        unassignShift={unassignShift}
      />
    </div>
    :
    <PageLoadingCircle />
  
}

    </>
    

  )

}
