import { Box, Card, IconButton } from "@mui/material";
import React ,{ useState } from "react";
import { Draggable } from "react-beautiful-dnd";
// import { TaskCardDeleteButton } from "./TaskCardDeleteButton";
import { TaskCardTitle } from "./TaskCardTitle";
import { TaskAddInput } from "./TaskInputForm";
import { Tasks } from "./Tasks";

import DeleteIcon from '@mui/icons-material/Delete';


const title = ["掃除","洗濯","料理","仕事"]
const init = title.map((t,index) => ({ id: index,
    draggableId: `${index}`,
    text: t})
    )


    export const TaskCard = ({
        taskCardsList,
        setTaskCardsList,
        taskCard,
        index,
      }) => {
        const [inputText, setInputText] = useState("");
        const [taskList, setTaskList] = useState([]);
        return (
          <Draggable draggableId={taskCard.id} index={index}>
            {(provided) => (
              <div
                className="taskCard"
                ref={provided.innerRef}
                {...provided.draggableProps}
              >
                <div
                  className="taskCardTitleAndTaskCardDeleteButtonArea"
                  {...provided.dragHandleProps}
                >
                  <TaskCardTitle />
                  <TaskCardDeleteButton
                    taskCardsList={taskCardsList}
                    setTaskCardsList={setTaskCardsList}
                    taskCard={taskCard}
                  />
                </div>
                <TaskAddInput
                  inputText={inputText}
                  setInputText={setInputText}
                  setTaskList={setTaskList}
                  taskList={taskList}
                />
                <Tasks
                  inputText={inputText}
                  taskList={taskList}
                  setTaskList={setTaskList}
                />
              </div>
            )}
          </Draggable>
        );
      };
