import { Button, CircularProgress } from "@mui/material";
import React, { useEffect, useState, useContext } from "react";
import { deleteShift, determineShifts, fetchSubmittedShifts } from "../apis/shifts";
import { PageLoadingCircle } from "../components/shared/commonPatrs";
import { BadgeContext } from "../contexts/badge";
import { SnackbarContext } from '../contexts/snackBar';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import { myShifts } from '../urls/urlList';
import { set } from 'react-hook-form';


import { ShiftEdit } from "./ShiftEdit";
import { dayOfWeek } from '../components/const';


export const Shift = () => {

  
const today = new Date();

const [value, setValue] = useState();
const [loading, setLoading] = useState(false)
const sb = useContext(SnackbarContext);
const badge = useContext(BadgeContext);
const [selectedDate, setSelectedDate] = useState(today);

const undo = () => {
  fetchSubmittedShifts()
  .then(res =>  setValue(res.data.shifts))
}

  
const updateShifts = (params) => {
  setLoading(true)
  determineShifts(params)
  .then(res => {
        if(res.status !== 200){ return sb.setSnackBar({open: true, variant:"error",content:"シフトを確定できませんでした。"}) }
        setValue(res.data.shifts)
        sb.setSnackBar({open: true, variant:"success",content:"シフトを確定しました。"})
        badge.setBadge({
          ...badge.badge, shift: res.data.shifts.filter(elm => elm.confirmed == false).length
        })
  })
  setLoading(false)
}

const renderMonth = (date) => {
  console.log(selectedDate.getFullYear())
  const month = [];
  const lastDay = new Date(date.getFullYear(), date.getMonth()+1,0);
  for(let i=1;i<=lastDay.getDate();i++){
    const targetDay = new Date(date.getFullYear(), date.getMonth(),i);
    month.push(
      {id: i,
       label: targetDay.getDate().toString()+`(${dayOfWeek[targetDay.getDay()]})`,
       minWidth: 100
      }
    )
  }

  return month;

}

const collectShift = (params) => {
   

  const ids =  [...new Set(value.map(s => s.employee_id))]
  const list = []
  ids.map(id => {
    list.push(params.filter(s => s.employee_id == id));
  })

  return list;
}


useEffect(() => {
  try {
      fetchSubmittedShifts()
      .then((res) => {
      setValue(res.data.shifts)
})
} 
catch(e){
  console.log(e)
}

},[])

  return (
    <>
    {
    value ?

    <div>
      <TableContainer style={{maxHeight: 400}}>
            <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              <TableCell
                  style={{ minWidth: 50 }}
                >
                  <span>ID</span>
                </TableCell>
              {renderMonth(selectedDate).map(column => (
                <TableCell
                  key={column.id}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
          {
          collectShift(value).map((row,i) => (
            <TableRow key={i}>
              <TableCell
                  key={i}
                >
                  {row[0].employee_id}
                </TableCell>
             {row.map(elm => (
                <TableCell
                  key={elm.id}
                >
                  {elm.attendance_time}-{elm.leaving_time}
                </TableCell>
              ))}  
            </TableRow>
          )
            )
          }
        </TableBody>
        </Table>
        </TableContainer>
      {/* <ShiftEdit
        value={value}
        loading={loading}
        setLoading={setLoading}
        undo={undo}
        updateShifts={updateShifts}
        unassignShift={unassignShift}
      /> */}
    </div>
    :
    <PageLoadingCircle />
  
}

    </>
    

  )

}
