import { Box, Stack } from "@mui/material";
import React, { useState, useContext, useEffect, useMemo } from "react";
import { AuthContext } from "../../contexts/auth";

import ChatTemplate from "./ChatTemplate"

const height =  document.documentElement.clientHeight;

export const ChatMessages = ({
    messages, 
    selected, 
    cancelSend ,
    tempId,
}) => {

   return (
        <Box  
            sx={{padding:"150px 2rem 0",
            height:`${height-100}px`,
            width:"100%",
            overflow: "scroll"
            }}
            >
             {messages.map((message, index) => {
            
                const side = message.employee_id === tempId ? "right" : "left";
                const baseTime = new Date(message.created_at)
                const time = `${("00"+baseTime.getHours()).slice(-2)} :${("00" + baseTime.getMinutes()).slice(-2)}`

                return (
                <div key={index}>
                    <h4>{baseTime.getMonth()}</h4>
                     <ChatTemplate
                        message={message}
                        roomId={selected.id}
                        side={side}
                        time={time}
                        cancelSend={cancelSend}
                    />
                </div>
                )
                })}

        </Box>)


}
